import { useEffect, useState } from 'react'
import Select from 'react-select'
const ExchangeForm = () => {
    const [modules, setModules] = useState(null);
    const [module, setModule] = useState('');
    const [slots, setSlots] = useState(null);
    useEffect(() => {
        const fetchModules = async () => {
            const response = await fetch('https://api.nusmods.com/v2/2023-2024/moduleList.json');
            const json = await response.json();
            const arr = json.map((module) => ({value: module.moduleCode, label: module.moduleCode}));
            if (response.ok) {
                setModules(arr);
            }
        }
        fetchModules();
    }, []);
    return (
        <form className='exchange'>
            <h3>Select a module</h3>
            {modules && <Select value={module} options={modules} onChange={setModule}/>}
            {(module != '') && 
            <h3>Select a slot</h3>
            }
        </form>
    )
}

export default ExchangeForm;
