import { useEffect, useState } from 'react'
import Select from 'react-select'
const ExchangeForm = () => {
    const [modules, setModules] = useState(null);
    const [module, setModule] = useState('');
    const [slots, setSlots] = useState(null);
    const [slot, setSlot] = useState(null);
    useEffect(() => {
        const fetchModules = async () => {
            const response = await fetch('https://api.nusmods.com/v2/2023-2024/moduleList.json');
            const json = await response.json();
            const arr = json.map((module) => ({value: module.moduleCode, label: module.moduleCode}));
            if (response.ok) {
                setModules(arr);
            }
        }
        const fetchSlots = async (module) => {
            const response = await fetch(`https://api.nusmods.com/v2/2023-2024/modules/${module}.json`);
            const json = await response.json();
            const arr = json.map((module) => ({value: module.moduleCode, label: module.moduleCode}));
            if (response.ok) {
                setSlots(arr);
            }
        }
        if (!modules) {
            fetchModules();
        }
        if (module != '') {
            fetchSlots();
        }
    }, [module]);
    
    return (
        <form className='exchange'>
            <h3>Select a module</h3>
            <p>{module}</p>
            {modules && <Select options={modules}/>}
            {(module != '') && 
            <div>
                <h3>Select a slot</h3>
                <Select value={slot} options={slots} onChange={setModule}/>
            </div>
            }
        </form>
    )
}

export default ExchangeForm;
